import React from 'react';
import './App.css';

class App extends React.Component{

    constructor(props){
        super(props);
        this.state={
            item:[],
            currentItem:{
                text:'',
                key:''
            }
        }
    }

    handleInput(e){
        this.setState({
            currentItem:{
                text:e.target.value,
                key:Date.now()
            }
        })
    }

    addItem(e){
        e.preventDefault();
        const newItem = this.state.currentItem;
        console.log(newItem);
    }

    render(){
        return(
            <div className="App">
                <header>
                    <form id="to-do-form" onSubmit={this.addItem}>
                        <div className="form-group">
                            <div className="row mx-auto">
                                <div className="col-9">
                                    <input className="form-control" type="text" placeholder="Enter Text" value={this.state.currentItem.text} onClick={this.handleInput}/>
                                </div>
                                <div className="col-3">
                                    <button type="submit" className="btn btn-primary">Add</button>
                                </div>
                            </div>
                        </div>
                    </form>
                </header>
            </div>
        );
    }
}
export default App;
