import React from 'react';
import './App.css';

class App extends React.Component{

    constructor(props){
        super(props);
        this.state={
            item:[],
            currentItem:{
                text:'',
                key:''
            }
        }
    }

    render(){
        return(
            <div className="App">
                <header>
                    <form id="to-do-form" method="post">
                        <div className="form-group">
                            <div className="row mx-auto">
                                <div className="col-9">
                                    <input className="form-control" type="text" placeholder="Enter Text" value={this.state.currentItem.text} />
                                </div>
                                <div className="col-3">
                                    <button type="submit" className="btn btn-primary">Add</button>
                                </div>
                            </div>
                        </div>
                    </form>
                </header>
            </div>
        );
    }
}
export default App;
